public partial class Form1 : Form\n{\n    private ErrorProvider errorProvider;\n\n    public Form1()\n    {\n        InitializeComponent();\n        errorProvider = new ErrorProvider();\n\n        // Subscribe to Validating and Validated events\n        textBox1.Validating += new CancelEventHandler(textBox1_Validating);\n        textBox1.Validated += new EventHandler(textBox1_Validated);\n    }\n\n    // Validating event handler\n    private void textBox1_Validating(object sender, CancelEventArgs e)\n    {\n        if (!int.TryParse(textBox1.Text, out _))\n        {\n            e.Cancel = true; // Prevents focus from leaving the TextBox\n            errorProvider.SetError(textBox1, "Please enter a valid number.");\n        }\n    }\n\n    // Validated event handler\n    private void textBox1_Validated(object sender, EventArgs e)\n    {\n        errorProvider.SetError(textBox1, string.Empty);\n    }\n}