public partial class Course\n{\n    private Category _category;\n\n    public Category Category\n    {\n        get\n        {\n            if (_category == null)\n            {\n                using (var context = new YourDataContext())\n                {\n                    _category = (from cc in context.CourseCategories\n                                 join c in context.Categories on cc.CategoryId equals c.CategoryId\n                                 where cc.CourseId == this.CourseId\n                                 select c).FirstOrDefault();\n                }\n            }\n            return _category;\n        }\n    }\n}