public class WebPageMonitor\n   {\n       public static TimeSpan MeasurePageLoadTime(string url)\n       {\n           // Set ChromeOptions to run Chrome in headless mode\n           var options = new ChromeOptions();\n           options.AddArgument("--headless");\n\n           // Initialize the ChromeDriver\n           using (var driver = new ChromeDriver(options))\n           {\n               // Start the stopwatch\n               var stopwatch = Stopwatch.StartNew();\n\n               // Navigate to the specified URL\n               driver.Navigate().GoToUrl(url);\n\n               // Wait until the page is fully loaded\n               var wait = new OpenQA.Selenium.Support.UI.WebDriverWait(driver, TimeSpan.FromSeconds(30));\n               wait.Until(d => ((IJavaScriptExecutor)d).ExecuteScript("return document.readyState").Equals("complete"));\n\n               // Stop the stopwatch\n               stopwatch.Stop();\n\n               // Return the elapsed time\n               return stopwatch.Elapsed;\n           }\n       }\n   }