// ORM generated entity class\npublic class UserEntity\n{\n    public int Id { get; set; }\n    public string Username { get; set; }\n    public string Email { get; set; }\n}\n\n// Manually created business class\npublic class User\n{\n    public int Id { get; set; }\n    public string Username { get; set; }\n    public string Email { get; set; }\n\n    // Business logic methods\n    public void UpdateEmail(string newEmail)\n    {\n        // Add business rule checks here\n        Email = newEmail;\n    }\n}\n\n// Example of mapping data from entity to business class\npublic User MapToBusinessClass(UserEntity entity)\n{\n    return new User\n    {\n        Id = entity.Id,\n        Username = entity.Username,\n        Email = entity.Email\n    };\n}\n\n// Example of using the ORM entity class directly\npublic void UpdateUserEmail(int userId, string newEmail)\n{\n    using (var context = new MyDbContext())\n    {\n        var userEntity = context.Users.Find(userId);\n        if (userEntity != null)\n        {\n            userEntity.Email = newEmail;\n            context.SaveChanges();\n        }\n    }\n}