using System;\nusing System.Web;\nusing System.Data.SqlClient;\nusing System.Configuration;\n\npublic class PdfHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Replace with your method to get the byte array from the database\n        byte[] pdfData = GetPdfFromDatabase();\n\n        if (pdfData != null)\n        {\n            context.Response.ContentType = "application/pdf";\n            context.Response.BinaryWrite(pdfData);\n            context.Response.End();\n        }\n        else\n        {\n            context.Response.StatusCode = 404;\n            context.Response.StatusDescription = "PDF not found";\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n\n    private byte[] GetPdfFromDatabase()\n    {\n        byte[] pdfData = null;\n        string connString = ConfigurationManager.ConnectionStrings["YourConnectionString"].ConnectionString;\n        string query = "SELECT PdfColumn FROM YourTable WHERE YourCondition";\n\n        using (SqlConnection conn = new SqlConnection(connString))\n        {\n            SqlCommand cmd = new SqlCommand(query, conn);\n            conn.Open();\n            SqlDataReader reader = cmd.ExecuteReader();\n\n            if (reader.Read())\n            {\n                pdfData = (byte[])reader["PdfColumn"];\n            }\n        }\n\n        return pdfData;\n    }\n}