using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create DataSet and Tables\n        DataSet dataSet = new DataSet();\n\n        DataTable table1 = new DataTable("Table1");\n        table1.Columns.Add("ID", typeof(int));\n        table1.Columns.Add("Name", typeof(string));\n\n        DataTable table2 = new DataTable("Table2");\n        table2.Columns.Add("ID", typeof(int));\n        table2.Columns.Add("Table1ID", typeof(int));\n        table2.Columns.Add("Detail", typeof(string));\n\n        // Add tables to DataSet\n        dataSet.Tables.Add(table1);\n        dataSet.Tables.Add(table2);\n\n        // Add some sample data\n        table1.Rows.Add(1, "John");\n        table1.Rows.Add(2, "Jane");\n\n        table2.Rows.Add(1, 1, "Detail for John");\n        table2.Rows.Add(2, 1, "Another detail for John");\n        table2.Rows.Add(3, 2, "Detail for Jane");\n\n        // Create a DataRelation\n        DataRelation relation = new DataRelation("Table1_Table2", table1.Columns["ID"], table2.Columns["Table1ID"]);\n        dataSet.Relations.Add(relation);\n\n        // Retrieve data from Table2 related to Table1\n        foreach (DataRow parentRow in table1.Rows)\n        {\n            Console.WriteLine($"Table1 ID: {parentRow["ID"]}, Name: {parentRow["Name"]}");\n\n            DataRow[] childRows = parentRow.GetChildRows(relation);\n\n            foreach (DataRow childRow in childRows)\n            {\n                Console.WriteLine($"  Table2 ID: {childRow["ID"]}, Detail: {childRow["Detail"]}");\n            }\n        }\n    }\n}