using System;\nusing System.Data.OleDb;\n\nclass WindowsSearch\n{\n    static void Main()\n    {\n        string query = "SELECT System.ItemName, System.ItemPathDisplay FROM SystemIndex WHERE System.ItemPathDisplay LIKE '%YourSearchTerm%'";\n\n        string connectionString = "Provider=Search.CollatorDSO;Extended Properties='Application=Windows';";\n\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            OleDbCommand command = new OleDbCommand(query, connection);\n            connection.Open();\n\n            using (OleDbDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    Console.WriteLine("File Name: " + reader["System.ItemName"]);\n                    Console.WriteLine("File Path: " + reader["System.ItemPathDisplay"]);\n                    Console.WriteLine();\n                }\n            }\n        }\n    }\n}