using ClamAV.Net;\nusing System;\nusing System.IO;\nusing System.Threading.Tasks;\n\npublic class VirusScanner\n{\n    private readonly ClamClient _clamClient;\n\n    public VirusScanner(string clamAvServer, int clamAvPort)\n    {\n        _clamClient = new ClamClient(clamAvServer, clamAvPort);\n    }\n\n    public async Task<bool> ScanFileAsync(string filePath)\n    {\n        try\n        {\n            if (!File.Exists(filePath))\n            {\n                throw new FileNotFoundException("File not found", filePath);\n            }\n\n            var scanResult = await _clamClient.ScanFileOnServerAsync(filePath);\n\n            if (scanResult.Result == ClamScanResults.Clean)\n            {\n                Console.WriteLine("File is clean.");\n                return true;\n            }\n            else\n            {\n                Console.WriteLine($"File is infected: {scanResult.RawResult}");\n                return false;\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n            return false;\n        }\n    }\n}