using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        \n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.InfoMessage += new SqlInfoMessageEventHandler(OnInfoMessage);\n            connection.Open();\n\n            using (SqlCommand command = new SqlCommand("your_non_query_command_here", connection))\n            {\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n\n    private static void OnInfoMessage(object sender, SqlInfoMessageEventArgs e)\n    {\n        Console.WriteLine("Message from SQL Server: " + e.Message);\n    }\n}