public class WebsitesController : Controller\n{\n    private static WebsiteList websites = new WebsiteList();\n\n    // GET: Websites\n    public ActionResult Index()\n    {\n        return View(websites);\n    }\n\n    // GET: Websites/Details/5\n    public ActionResult Details(int id)\n    {\n        var website = websites.FirstOrDefault(w => w.Id == id);\n        if (website == null)\n        {\n            return HttpNotFound();\n        }\n        return View(website);\n    }\n\n    // GET: Websites/Create\n    public ActionResult Create()\n    {\n        return View();\n    }\n\n    // POST: Websites/Create\n    [HttpPost]\n    public ActionResult Create(Website website)\n    {\n        if (ModelState.IsValid)\n        {\n            website.Id = websites.Max(w => w.Id) + 1;\n            websites.Add(website);\n            return RedirectToAction("Index");\n        }\n        return View(website);\n    }\n\n    // GET: Websites/Edit/5\n    public ActionResult Edit(int id)\n    {\n        var website = websites.FirstOrDefault(w => w.Id == id);\n        if (website == null)\n        {\n            return HttpNotFound();\n        }\n        return View(website);\n    }\n\n    // POST: Websites/Edit/5\n    [HttpPost]\n    public ActionResult Edit(Website website)\n    {\n        if (ModelState.IsValid)\n        {\n            var existingWebsite = websites.FirstOrDefault(w => w.Id == website.Id);\n            if (existingWebsite != null)\n            {\n                existingWebsite.Name = website.Name;\n                existingWebsite.Url = website.Url;\n            }\n            return RedirectToAction("Index");\n        }\n        return View(website);\n    }\n\n    // GET: Websites/Delete/5\n    public ActionResult Delete(int id)\n    {\n        var website = websites.FirstOrDefault(w => w.Id == id);\n        if (website == null)\n        {\n            return HttpNotFound();\n        }\n        return View(website);\n    }\n\n    // POST: Websites/Delete/5\n    [HttpPost, ActionName("Delete")]\n    public ActionResult DeleteConfirmed(int id)\n    {\n        var website = websites.FirstOrDefault(w => w.Id == id);\n        if (website != null)\n        {\n            websites.Remove(website);\n        }\n        return RedirectToAction("Index");\n    }\n}