using System;\n    using System.Collections.Generic;\n    using System.IO;\n    using Microsoft.VisualStudio.TextTemplating;\n\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Create your objects\n            var objects = new List<MyObject>\n            {\n                new MyObject { Name = "Object1", Description = "Description of Object1" },\n                new MyObject { Name = "Object2", Description = "Description of Object2" }\n            };\n\n            // Create the custom host\n            var host = new CustomHost(objects);\n\n            // Load the template\n            string templatePath = "PathToYourTemplate.tt";\n            string templateContent = File.ReadAllText(templatePath);\n\n            // Create the text templating engine\n            var engine = new Engine();\n\n            // Process the template with the custom host\n            string result = engine.ProcessTemplate(templateContent, host);\n\n            // Output the result\n            Console.WriteLine(result);\n        }\n    }