using System;\nusing System.Data.SqlClient;\n\npublic class FullTextSearch\n{\n    public void Search(string searchTerms)\n    {\n        // Split the search terms by comma and trim any whitespace\n        string[] terms = searchTerms.Split(new[] { ',' }, StringSplitOptions.RemoveEmptyEntries);\n        for (int i = 0; i < terms.Length; i++)\n        {\n            terms[i] = terms[i].Trim();\n        }\n\n        // Join the terms with AND operator for full text search\n        string searchCondition = string.Join(" AND ", terms);\n\n        string query = "SELECT col1 FROM myTable WHERE CONTAINS(fts_column, @searchCondition)";\n        \n        using (SqlConnection connection = new SqlConnection("your_connection_string"))\n        {\n            SqlCommand command = new SqlCommand(query, connection);\n            command.Parameters.AddWithValue("@searchCondition", searchCondition);\n            \n            connection.Open();\n            using (SqlDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    // Process each result\n                    Console.WriteLine(reader["col1"]);\n                }\n            }\n        }\n    }\n}