using System;\nusing System.Diagnostics;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string processName = "YourProcessName"; // Replace with your process name\n        PerformanceCounter cpuCounter = new PerformanceCounter("Process", "% Processor Time", processName, true);\n\n        // Give some time to get an accurate reading\n        Thread.Sleep(1000);\n\n        // Get the current value\n        float cpuUsage = cpuCounter.NextValue();\n        \n        // Sleep for another second to get a more accurate value\n        Thread.Sleep(1000);\n        cpuUsage = cpuCounter.NextValue();\n\n        Console.WriteLine($"CPU Usage for process {processName}: {cpuUsage}%");\n    }\n}