using System;\nusing System.IO;\nusing System.Linq;\nusing System.Reflection;\nusing System.Security.Cryptography;\n\npublic class ExampleClass\n{\n    public void ExampleMethod()\n    {\n        // Example method code\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        // Get the type of the class containing the method\n        Type type = typeof(ExampleClass);\n\n        // Get the method info for the particular method\n        MethodInfo methodInfo = type.GetMethod("ExampleMethod");\n\n        if (methodInfo != null)\n        {\n            // Get the module containing the method\n            Module module = methodInfo.Module;\n\n            // Get the IL bytes of the method\n            byte[] ilBytes = GetMethodILBytes(methodInfo);\n\n            if (ilBytes != null)\n            {\n                // Calculate the checksum\n                string checksum = CalculateChecksum(ilBytes);\n                Console.WriteLine($"Checksum: {checksum}");\n            }\n        }\n    }\n\n    static byte[] GetMethodILBytes(MethodInfo methodInfo)\n    {\n        // Get the method body\n        MethodBody methodBody = methodInfo.GetMethodBody();\n        if (methodBody != null)\n        {\n            return methodBody.GetILAsByteArray();\n        }\n        return null;\n    }\n\n    static string CalculateChecksum(byte[] data)\n    {\n        using (SHA256 sha256 = SHA256.Create())\n        {\n            byte[] hash = sha256.ComputeHash(data);\n            return BitConverter.ToString(hash).Replace("-", "").ToLower();\n        }\n    }\n}