[ServiceContract]\npublic interface IMyService\n{\n    [OperationContract]\n    [WebInvoke(Method = "POST", UriTemplate = "/query", BodyStyle = WebMessageBodyStyle.Wrapped)]\n    [FormUrlEncodedOperationBehavior]\n    XElement Query_Post(string query, string[] @params);\n}\n\npublic class MyService : IMyService\n{\n    public XElement Query_Post(string query, string[] @params)\n    {\n        // Your implementation here\n        return new XElement("Response", new XElement("Query", query), new XElement("Params", string.Join(", ", @params)));\n    }\n}