using Microsoft.Win32;\nusing System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string fileExtension = ".jpg";\n        string associatedApp = GetAssociatedApplication(fileExtension);\n\n        if (!string.IsNullOrEmpty(associatedApp))\n        {\n            Console.WriteLine($"Associated application for {fileExtension}: {associatedApp}");\n            LaunchApplication(associatedApp, "C:\\path\\to\\your\\file.jpg"); // Provide the actual path to your file\n        }\n        else\n        {\n            Console.WriteLine($"No application associated with {fileExtension}");\n        }\n    }\n\n    static string GetAssociatedApplication(string fileExtension)\n    {\n        string progId = null;\n        string appPath = null;\n\n        // Find the ProgID associated with the file extension\n        using (RegistryKey extKey = Registry.ClassesRoot.OpenSubKey(fileExtension))\n        {\n            if (extKey != null)\n            {\n                progId = extKey.GetValue("") as string;\n            }\n        }\n\n        if (!string.IsNullOrEmpty(progId))\n        {\n            // Find the application associated with the ProgID\n            using (RegistryKey progIdKey = Registry.ClassesRoot.OpenSubKey(progId + @"\shell\open\command"))\n            {\n                if (progIdKey != null)\n                {\n                    appPath = progIdKey.GetValue("") as string;\n                    if (appPath != null)\n                    {\n                        // Remove quotes around the path if present\n                        appPath = appPath.Trim('"');\n                    }\n                }\n            }\n        }\n\n        return appPath;\n    }\n\n    static void LaunchApplication(string appPath, string filePath)\n    {\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = appPath,\n            Arguments = $"\"{filePath}\""\n        };\n\n        try\n        {\n            Process.Start(startInfo);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error launching application: {ex.Message}");\n        }\n    }\n}