using System;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example Unicode string containing "ê"\n        string unicodeString = "ê";\n\n        // Convert Unicode string to extended ASCII bytes\n        Encoding asciiEncoding = Encoding.GetEncoding(\n            "ISO-8859-1", // Extended ASCII encoding\n            new EncoderReplacementFallback("?"), // Replacement fallback for unsupported characters\n            new DecoderReplacementFallback("?"));\n        \n        byte[] asciiBytes = asciiEncoding.GetBytes(unicodeString);\n\n        // Print the byte values\n        foreach (byte b in asciiBytes)\n        {\n            Console.WriteLine(b); // Expected output for "ê" is 136\n        }\n\n        // Convert bytes back to string\n        string asciiString = asciiEncoding.GetString(asciiBytes);\n        Console.WriteLine(asciiString); // Output the converted string\n    }\n}