using System;\nusing System.Net;\n\npublic class IPv6Parser\n{\n    public static (long, long) ParseIPv6ToInt64(string ipv6Address)\n    {\n        IPAddress ip = IPAddress.Parse(ipv6Address);\n        byte[] bytes = ip.GetAddressBytes();\n        \n        if (bytes.Length != 16)\n        {\n            throw new ArgumentException("Invalid IPv6 address.");\n        }\n\n        long high = BitConverter.ToInt64(bytes, 0);\n        long low = BitConverter.ToInt64(bytes, 8);\n\n        return (high, low);\n    }\n    \n    public static void Main()\n    {\n        string ipv6Address = "2001:0db8:85a3:0000:0000:8a2e:0370:7334";\n        var (high, low) = ParseIPv6ToInt64(ipv6Address);\n        \n        Console.WriteLine($"High: {high}");\n        Console.WriteLine($"Low: {low}");\n    }\n}