protected void Page_Load(object sender, EventArgs e)\n{\n    // Generate a unique ID for each control instance\n    string uniqueId = Guid.NewGuid().ToString("N");\n\n    // Register the startup script with the unique ID\n    string script = $@"\n        var opts_{uniqueId} = {{ x: 99 }};\n        var myObject_{uniqueId} = new MyObject(opts_{uniqueId});\n    ";\n    ClientScript.RegisterStartupScript(this.GetType(), "InitScript" + uniqueId, script, true);\n}