using System;\nusing Gtk;\n\npublic class ScrolledWindowExample : Window\n{\n    public ScrolledWindowExample() : base("ScrolledWindow Example")\n    {\n        SetDefaultSize(400, 300);\n        SetPosition(WindowPosition.Center);\n\n        ScrolledWindow scrolledWindow = new ScrolledWindow();\n        TextView textView = new TextView();\n\n        // Add some sample text to the TextView\n        textView.Buffer.Text = new string('A', 1000);\n\n        scrolledWindow.Add(textView);\n        Add(scrolledWindow);\n\n        // Get the horizontal and vertical Adjustment objects\n        Adjustment hadjustment = scrolledWindow.Hadjustment;\n        Adjustment vadjustment = scrolledWindow.Vadjustment;\n\n        // Connect event handlers to the ValueChanged event\n        hadjustment.ValueChanged += OnScrollChanged;\n        vadjustment.ValueChanged += OnScrollChanged;\n\n        ShowAll();\n    }\n\n    private void OnScrollChanged(object sender, EventArgs e)\n    {\n        Adjustment adjustment = (Adjustment)sender;\n        Console.WriteLine($"Scrolled: Value={adjustment.Value}");\n    }\n\n    public static void Main()\n    {\n        Application.Init();\n        new ScrolledWindowExample();\n        Application.Run();\n    }\n}