using (OracleConnection conn = new OracleConnection("your_connection_string"))\n    {\n        using (OracleCommand cmd = new OracleCommand("your_stored_procedure", conn))\n        {\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.Add("your_ref_cursor_parameter", OracleDbType.RefCursor).Direction = ParameterDirection.Output;\n\n            OracleDataAdapter da = new OracleDataAdapter(cmd);\n            DataSet ds = new DataSet();\n            conn.Open();\n            da.Fill(ds);\n        }\n    }