using System;\nusing System.Runtime.InteropServices;\nusing System.Web;\n\nnamespace MyNamespace\n{\n    [ComVisible(true)]\n    [ClassInterface(ClassInterfaceType.None)]\n    public class MyClass : IMyClass\n    {\n        public string GetSpecialCookieValue()\n        {\n            HttpContext context = HttpContext.Current;\n            if (context != null && context.Request.Cookies["SpecialCookie"] != null)\n            {\n                return context.Request.Cookies["SpecialCookie"].Value;\n            }\n            return string.Empty;\n        }\n    }\n\n    [ComVisible(true)]\n    [InterfaceType(ComInterfaceType.InterfaceIsIDispatch)]\n    public interface IMyClass\n    {\n        string GetSpecialCookieValue();\n    }\n}