using System;\nusing System.IO;\n\npublic class FileComparer\n{\n    public static bool AreFilesEqual(string filePath1, string filePath2)\n    {\n        const int bufferSize = 1024 * 1024; // 1MB buffer size\n        byte[] buffer1 = new byte[bufferSize];\n        byte[] buffer2 = new byte[bufferSize];\n\n        using (FileStream fs1 = new FileStream(filePath1, FileMode.Open, FileAccess.Read))\n        using (FileStream fs2 = new FileStream(filePath2, FileMode.Open, FileAccess.Read))\n        {\n            if (fs1.Length != fs2.Length)\n                return false;\n\n            int bytesRead1, bytesRead2;\n            while ((bytesRead1 = fs1.Read(buffer1, 0, bufferSize)) > 0)\n            {\n                bytesRead2 = fs2.Read(buffer2, 0, bufferSize);\n                if (bytesRead1 != bytesRead2)\n                    return false;\n\n                for (int i = 0; i < bytesRead1; i++)\n                {\n                    if (buffer1[i] != buffer2[i])\n                        return false;\n                }\n            }\n        }\n\n        return true;\n    }\n\n    public static void Main()\n    {\n        string filePath1 = "path/to/first/file";\n        string filePath2 = "path/to/second/file";\n\n        bool areEqual = AreFilesEqual(filePath1, filePath2);\n        Console.WriteLine("Files are " + (areEqual ? "identical" : "different"));\n    }\n}