// In Welcome.aspx.cs\nprotected void Page_Load(object sender, EventArgs e)\n{\n    var sessionData = "your data";\n    Session["sessionData"] = sessionData;\n\n    // Set session cookie manually\n    HttpCookie cookie = new HttpCookie("ASP.NET_SessionId", Session.SessionID);\n    cookie.HttpOnly = true;\n    cookie.Path = "/";\n    Response.Cookies.Add(cookie);\n\n    Response.Redirect("~/Models.aspx");\n}\n\n// In Models.aspx.cs\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (Session["sessionData"] != null)\n    {\n        // Session data is available\n        var sessionData = Session["sessionData"].ToString();\n        Response.Write("Session data: " + sessionData);\n    }\n    else\n    {\n        // Session data is not available\n        Response.Write("No session data available.");\n    }\n}