// Define a business object (entity)\npublic class Employee\n{\n    public virtual int Id { get; set; }\n    public virtual string Name { get; set; }\n    public virtual Department Department { get; set; }\n}\n\n// Define a mapping for the business object\npublic class EmployeeMap : ClassMapping<Employee>\n{\n    public EmployeeMap()\n    {\n        Table("Employees");\n        Id(x => x.Id, m => m.Generator(Generators.Identity));\n        Property(x => x.Name, m => m.Length(50));\n        ManyToOne(x => x.Department, m =>\n        {\n            m.Column("DepartmentId");\n            m.Cascade(Cascade.None);\n        });\n    }\n}\n\n// Configuration and session factory setup\nvar configuration = new Configuration();\nconfiguration.Configure(); // Configure from App.config / Web.config\nconfiguration.AddAssembly(typeof(Employee).Assembly);\n\nvar sessionFactory = configuration.BuildSessionFactory();\n\n// Usage example\nusing (var session = sessionFactory.OpenSession())\n{\n    using (var transaction = session.BeginTransaction())\n    {\n        var employee = new Employee\n        {\n            Name = "John Doe",\n            Department = session.Get<Department>(1) // Assuming department with ID 1 exists\n        };\n        session.Save(employee);\n        transaction.Commit();\n    }\n}