using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            // Query for network drives using WMI\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher("SELECT * FROM Win32_LogicalDisk WHERE DriveType=4");\n            foreach (ManagementObject queryObj in searcher.Get())\n            {\n                // Get the device ID (drive letter) and volume name\n                string deviceID = queryObj["DeviceID"].ToString();\n                string volumeName = queryObj["VolumeName"]?.ToString() ?? "No Label";\n\n                Console.WriteLine($"Drive {deviceID}: Volume Label = {volumeName}");\n            }\n        }\n        catch (ManagementException e)\n        {\n            Console.WriteLine("An error occurred while querying for WMI data: " + e.Message);\n        }\n    }\n}