using System;\nusing edu.mit.jwi;\nusing edu.mit.jwi.item;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Path to the WordNet dictionary\n        string dictPath = @"C:\WordNet\dict";\n        var wordNetDir = new java.io.File(dictPath);\n\n        // Construct the dictionary object and open it\n        IDictionary dict = new edu.mit.jwi.Dictionary(wordNetDir);\n        dict.open();\n\n        // Query words of specific length\n        int wordLength = 5; // Example: words of length 5\n        foreach (var word in dict.getWords())\n        {\n            if (word.Length == wordLength)\n            {\n                Console.WriteLine(word);\n            }\n        }\n\n        // Partial match example\n        string partialMatch = "cat"; // Example: words containing "cat"\n        foreach (var word in dict.getWords())\n        {\n            if (word.Contains(partialMatch))\n            {\n                Console.WriteLine(word);\n            }\n        }\n\n        // Close the dictionary\n        dict.close();\n    }\n}