using System.Net.Http;\n   using System.Threading.Tasks;\n\n   public async Task<string> GetShortenedChartUrl(string chartData)\n   {\n       using (var client = new HttpClient())\n       {\n           var content = new StringContent(chartData);\n           var response = await client.PostAsync("https://chart.googleapis.com/chart", content);\n           response.EnsureSuccessStatusCode();\n           return await response.Content.ReadAsStringAsync();\n       }\n   }