private void dataGridView1_RowPrePaint(object sender, DataGridViewRowPrePaintEventArgs e)\n{\n    if (dataGridView1.Rows[e.RowIndex].Selected)\n    {\n        // Define the rectangle area for the selected row\n        Rectangle rowBounds = new Rectangle(\n            dataGridView1.RowHeadersWidth,\n            e.RowBounds.Top,\n            dataGridView1.Columns.GetColumnsWidth(DataGridViewElementStates.Visible) - dataGridView1.HorizontalScrollingOffset + 1,\n            e.RowBounds.Height - 1);\n\n        // Draw the border\n        using (Pen pen = new Pen(Color.Black, 2)) // Choose the color and width of the border\n        {\n            e.Graphics.DrawRectangle(pen, rowBounds);\n        }\n\n        // Prevent default selection drawing\n        e.Handled = true;\n    }\n}\n\nprivate void Form1_Load(object sender, EventArgs e)\n{\n    // Subscribe to the RowPrePaint event\n    dataGridView1.RowPrePaint += dataGridView1_RowPrePaint;\n\n    // Set other properties to ensure the row back color does not change on selection\n    dataGridView1.SelectionMode = DataGridViewSelectionMode.FullRowSelect;\n    dataGridView1.DefaultCellStyle.SelectionBackColor = dataGridView1.DefaultCellStyle.BackColor;\n    dataGridView1.DefaultCellStyle.SelectionForeColor = dataGridView1.DefaultCellStyle.ForeColor;\n}