using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass WebServiceClient\n{\n    private static readonly string[] serverUrls = {\n        "https://server1.example.com/service",\n        "https://server2.example.com/service",\n        "https://server3.example.com/service"\n    };\n\n    public static async Task Main(string[] args)\n    {\n        HttpClient client = new HttpClient();\n        \n        foreach (string url in serverUrls)\n        {\n            try\n            {\n                HttpResponseMessage response = await client.GetAsync(url);\n                if (response.IsSuccessStatusCode)\n                {\n                    // Process the response if the request is successful\n                    string result = await response.Content.ReadAsStringAsync();\n                    Console.WriteLine($"Successful response from {url}: {result}");\n                    break;\n                }\n            }\n            catch (HttpRequestException ex)\n            {\n                Console.WriteLine($"Failed to connect to {url}: {ex.Message}");\n                // Log the exception or handle as necessary\n            }\n        }\n    }\n}