using DocumentFormat.OpenXml.Packaging;\nusing DocumentFormat.OpenXml.Wordprocessing;\nusing System;\nusing System.IO;\nusing System.Linq;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string filePath = "path_to_your_word_document.docx";\n        ExtractEquationsAndImages(filePath);\n    }\n\n    static void ExtractEquationsAndImages(string filePath)\n    {\n        using (WordprocessingDocument wordDoc = WordprocessingDocument.Open(filePath, false))\n        {\n            // Extracting equations\n            var equations = wordDoc.MainDocumentPart.Document.Descendants<DocumentFormat.OpenXml.Math.OfficeMath>();\n            int eqIndex = 1;\n            foreach (var equation in equations)\n            {\n                string eqText = equation.InnerText;\n                Console.WriteLine($"Equation {eqIndex}: {eqText}");\n                eqIndex++;\n            }\n\n            // Extracting images\n            var imageParts = wordDoc.MainDocumentPart.ImageParts;\n            int imgIndex = 1;\n            foreach (var imagePart in imageParts)\n            {\n                string imageFilePath = Path.Combine("output_directory", $"Image{imgIndex}.jpg");\n                using (FileStream fileStream = new FileStream(imageFilePath, FileMode.Create))\n                {\n                    imagePart.GetStream().CopyTo(fileStream);\n                }\n                Console.WriteLine($"Extracted Image {imgIndex} to {imageFilePath}");\n                imgIndex++;\n            }\n        }\n    }\n}