using Saxon.Api;\n\npublic void TransformUsingSaxon(string xmlPath, string xsltPath, string outputPath)\n{\n    Processor processor = new Processor();\n    XsltCompiler compiler = processor.NewXsltCompiler();\n    XsltExecutable executable = compiler.Compile(new Uri(xsltPath));\n    XsltTransformer transformer = executable.Load();\n\n    XdmNode input = processor.NewDocumentBuilder().Build(new Uri(xmlPath));\n    transformer.InitialContextNode = input;\n\n    Serializer serializer = new Serializer();\n    serializer.SetOutputFile(outputPath);\n    \n    transformer.Run(serializer);\n}