using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = @"C:\path\to\your\dllfile.dll"; // Update this path to the actual DLL file path\n\n        FileVersionInfo fileInfo = FileVersionInfo.GetVersionInfo(filePath);\n\n        Console.WriteLine("File: " + fileInfo.FileName);\n        Console.WriteLine("Product Name: " + fileInfo.ProductName);\n        Console.WriteLine("Product Version: " + fileInfo.ProductVersion);\n        Console.WriteLine("File Description: " + fileInfo.FileDescription);\n        Console.WriteLine("File Version: " + fileInfo.FileVersion);\n        Console.WriteLine("Company Name: " + fileInfo.CompanyName);\n    }\n}