<%@ Page Language="C#" %>\n<%@ Import Namespace="System.IO" %>\n<%@ Import Namespace="System.Runtime.InteropServices" %>\n\n<script runat="server">\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string reportFile = GenerateAccessReport();\n        if (!string.IsNullOrEmpty(reportFile))\n        {\n            Response.ContentType = "application/octet-stream";\n            Response.AppendHeader("Content-Disposition", $"attachment; filename={Path.GetFileName(reportFile)}");\n            Response.TransmitFile(reportFile);\n            Response.End();\n        }\n        else\n        {\n            Response.Write("Error generating report.");\n        }\n    }\n\n    private string GenerateAccessReport()\n    {\n        string dbPath = Server.MapPath("~/App_Data/YourDatabase.accdb");\n        string reportPath = Server.MapPath("~/Reports/Report.pdf");\n        string accessAppPath = @"C:\Path\To\MSAccess.exe"; // Path to MS Access executable\n\n        var accessApp = new Microsoft.Office.Interop.Access.Application();\n        try\n        {\n            accessApp.OpenCurrentDatabase(dbPath);\n            accessApp.DoCmd.OpenReport("YourReportName", Microsoft.Office.Interop.Access.AcView.acViewPreview);\n            accessApp.DoCmd.OutputTo(Microsoft.Office.Interop.Access.AcOutputObjectType.acOutputReport, "YourReportName", "PDFFormat(*.pdf)", reportPath);\n            return reportPath;\n        }\n        catch (COMException ex)\n        {\n            // Handle exception\n            return null;\n        }\n        finally\n        {\n            accessApp.Quit();\n            Marshal.ReleaseComObject(accessApp);\n        }\n    }\n</script>