using System;\n\nnamespace ConditionalCompilationExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            Console.WriteLine("This code runs in all builds.");\n\n#if FULL_BUILD\n            Console.WriteLine("This code runs only in the full build.");\n#endif\n\n#if PARTIAL_BUILD\n            Console.WriteLine("This code runs only in the partial build.");\n#endif\n        }\n    }\n}