protected void cmdAddGift_Click(object sender, EventArgs e)\n{\n    using (OleDbConnection objConn = new OleDbConnection(DataSource))\n    {\n        Random r = new Random();\n        int giftID = r.Next(1200, 14000);\n\n        string query = "Insert into Gifts (GiftID, Description) values (@GiftID, @Description)";\n        OleDbCommand objCommand = new OleDbCommand(query, objConn);\n\n        objCommand.Parameters.AddWithValue("@GiftID", giftID);\n        objCommand.Parameters.AddWithValue("@Description", txtNewGift.Text);\n\n        objConn.Open();\n        objCommand.ExecuteNonQuery();\n        objConn.Close();\n    }\n\n    PopulateGifts();\n    txtNewGift.Text = "";\n    UpdatePanel3.Update();\n}