using (SPSite site = new SPSite("http://yoursite"))\n{\n    using (SPWeb web = site.OpenWeb())\n    {\n        SPList list = web.Lists["Announcements"];\n        \n        foreach (var announcementData in newAnnouncements)\n        {\n            SPListItem item = list.AddItem();\n            item["Title"] = announcementData.Key;\n            item["Created By"] = web.EnsureUser(announcementData.Value); // Ensure user gets the SPUser object\n            item.SystemUpdate(); // Use SystemUpdate to update system fields without modifying other fields\n        }\n    }\n}