// Data Layer\npublic class DataLayer\n{\n    public DataTable GetDataFromDatabase()\n    {\n        // Code to interact with the database and return a DataTable\n    }\n}\n\n// Business Layer\npublic class BusinessLayer\n{\n    private DataLayer dataLayer;\n\n    public BusinessLayer()\n    {\n        dataLayer = new DataLayer();\n    }\n\n    public DataSet GetBusinessData()\n    {\n        DataTable rawData = dataLayer.GetDataFromDatabase();\n        \n        // Business logic to manipulate and organize raw data into a DataSet\n        DataSet businessData = new DataSet();\n        businessData.Tables.Add(rawData);\n\n        return businessData;\n    }\n}