using System;\nusing System.IO;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string directoryPath = @"C:\YourDirectory";\n        string fileName = "FooBar";\n        string fileExtension = ".xml";\n        \n        string uniqueFileName = GetUniqueFileName(directoryPath, fileName, fileExtension);\n        Console.WriteLine("Unique Filename: " + uniqueFileName);\n    }\n    \n    public static string GetUniqueFileName(string directoryPath, string fileName, string fileExtension)\n    {\n        string fullPath = Path.Combine(directoryPath, fileName + fileExtension);\n        int count = 1;\n        \n        while (File.Exists(fullPath))\n        {\n            string tempFileName = $"{fileName}({count})";\n            fullPath = Path.Combine(directoryPath, tempFileName + fileExtension);\n            count++;\n        }\n        \n        return fullPath;\n    }\n}