using System;\nusing System.Security.Cryptography;\n\npublic class EncryptionLengthCalculator\n{\n    public static int GetCipherTextLength(int plainTextLength, int blockSize = 16)\n    {\n        // Calculate the padded plaintext length\n        int paddedPlainTextLength = plainTextLength;\n        if (plainTextLength % blockSize != 0)\n        {\n            paddedPlainTextLength = (plainTextLength / blockSize + 1) * blockSize;\n        }\n\n        // Calculate the Base64 encoded length\n        int cipherTextLength = paddedPlainTextLength; // Ciphertext length is the same as padded plaintext length\n        int base64EncodedLength = (int)Math.Ceiling((cipherTextLength / 3.0) * 4);\n\n        return base64EncodedLength;\n    }\n\n    public static void Main()\n    {\n        int plainTextLength = 100; // Example plaintext length\n        int cipherTextLength = GetCipherTextLength(plainTextLength);\n        Console.WriteLine($"Cipher text length for plain text of length {plainTextLength}: {cipherTextLength}");\n    }\n}