using System;\nusing DotNetWikiBot;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize the site object with the URL of the Mediawiki site\n        Site site = new Site("https://en.wikipedia.org", "username", "password");\n\n        // Load a page from the site\n        Page page = new Page(site, "PageTitle");\n\n        // Fetch the page content\n        page.Load();\n\n        // Get the Mediawiki markup\n        string mediawikiMarkup = page.text;\n\n        // Convert Mediawiki markup to HTML\n        string htmlContent = ConvertToHtml(mediawikiMarkup);\n\n        // Output the HTML content\n        Console.WriteLine(htmlContent);\n    }\n\n    static string ConvertToHtml(string mediawikiMarkup)\n    {\n        // Implement your own conversion logic or use an existing library if available\n        // For simplicity, this is just a placeholder for actual conversion logic\n        return "<html>" + mediawikiMarkup + "</html>";\n    }\n}