public bool VerifySecretAnswer(string userName, string secretAnswer)\n{\n    try\n    {\n        // Get the user\n        MembershipUser user = Membership.GetUser(userName);\n        if (user == null)\n        {\n            return false;\n        }\n\n        // Unlock the user if locked\n        if (user.IsLockedOut)\n        {\n            user.UnlockUser();\n        }\n\n        // Attempt to reset the password with the provided secret answer\n        string newPassword = Membership.Provider.ResetPassword(userName, secretAnswer);\n\n        // If the reset was successful, the secret answer was correct\n        return !string.IsNullOrEmpty(newPassword);\n    }\n    catch (MembershipPasswordException)\n    {\n        // Secret answer was incorrect\n        return false;\n    }\n    catch (Exception)\n    {\n        // Handle other exceptions\n        throw;\n    }\n}