using System;\nusing System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string urlPart = "Your URL Part Here";\n        string cleanUrl = CleanUrl(urlPart);\n        Console.WriteLine(cleanUrl);\n    }\n\n    public static string CleanUrl(string urlPart)\n    {\n        urlPart = urlPart.ToLower();\n        urlPart = Regex.Replace(urlPart, @"[^a-z0-9\- ]", string.Empty);\n        urlPart = Regex.Replace(urlPart, @"[ \-]+", "-");\n        return urlPart;\n    }\n}