// Slave Code\nusing System;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class Slave\n{\n    private string masterIp;\n    private int masterPort;\n\n    public Slave(string ip, int port)\n    {\n        masterIp = ip;\n        masterPort = port;\n    }\n\n    public void Synchronize()\n    {\n        TcpClient client = new TcpClient(masterIp, masterPort);\n        NetworkStream stream = client.GetStream();\n\n        byte[] data = new byte[256];\n        int bytes = stream.Read(data, 0, data.Length);\n        string responseData = Encoding.UTF8.GetString(data, 0, bytes);\n\n        DateTime masterTime = DateTime.Parse(responseData);\n        // Adjust local clock or video playback timing to match masterTime\n        // Implement video synchronization logic here\n\n        client.Close();\n    }\n}