using Gtk;\n\npublic class MyApp\n{\n    public static void Main()\n    {\n        Application.Init();\n        \n        Window window = new Window("Original Window");\n        VBox vbox = new VBox();\n        \n        Label label = new Label("This is the original window content");\n        vbox.PackStart(label, true, true, 0);\n        \n        window.Add(vbox);\n        window.ShowAll();\n\n        Window notebookWindow = new Window("Notebook Window");\n        Notebook notebook = new Notebook();\n        \n        VBox newVBox = (VBox) vbox;\n        vbox.Remove(label); // Remove widget from the original container\n        \n        notebook.AppendPage(newVBox, new Label("Tab 1"));\n        notebookWindow.Add(notebook);\n        notebookWindow.ShowAll();\n        \n        Application.Run();\n    }\n}