using System.IO.Ports;\n\nclass PoleDisplay : SerialPort\n{\n    private SerialPort srPort = null;\n\n    public PoleDisplay()\n    {\n        try\n        {\n            srPort = new SerialPort("COM1", 9600, Parity.None, 8, StopBits.One);\n            if (!srPort.IsOpen) srPort.Open();\n        }\n        catch { }\n    }\n\n    ~PoleDisplay()\n    {\n        srPort.Close();\n    }\n\n    // To clear the display\n    public void ClearDisplay()\n    {\n        srPort.Write(new byte[] { 0x0C }, 0, 1); // 0x0C is the Form Feed command to clear display\n    }\n\n    // To set cursor position\n    public void SetCursorPosition(int line, int position)\n    {\n        // Assuming your pole display uses specific commands to set cursor position\n        // These commands vary by manufacturer, so consult your display's manual\n        byte[] command = new byte[] { 0x1B, 0x6C, (byte)line, (byte)position }; // Example command\n        srPort.Write(command, 0, command.Length);\n    }\n\n    // Display Function\n    // 'line' 1 for First line and 0 For second line\n    public void Display(string textToDisplay, int line)\n    {\n        if (line == 0)\n            srPort.Write(textToDisplay);\n        else\n            srPort.WriteLine(textToDisplay);\n    }\n}