using Microsoft.AspNetCore.Mvc;\nusing System.IO;\n\nnamespace BitmapWebAPI.Controllers\n{\n    [Route("api/[controller]")]\n    [ApiController]\n    public class BitmapController : ControllerBase\n    {\n        [HttpGet]\n        public IActionResult GetBitmap()\n        {\n            string filePath = @"\\network\path\to\image.bmp";\n            if (System.IO.File.Exists(filePath))\n            {\n                byte[] imageBytes = System.IO.File.ReadAllBytes(filePath);\n                return File(imageBytes, "image/bmp");\n            }\n\n            return NotFound();\n        }\n    }\n}