using SubSonic.Repository;\n\n// Create the repository\nSimpleRepository repo = new SimpleRepository(SimpleRepositoryOptions.RunMigrations);\n\n// Saving a Posting with Comments\nPosting post = new Posting \n{\n    InternalId = Guid.NewGuid(),\n    Title = "Sample Post",\n    Body = "This is a sample post body",\n    PostingDate = DateTime.Now,\n    Comments = new List<Comment>\n    {\n        new Comment { CommentId = Guid.NewGuid(), Body = "First comment" },\n        new Comment { CommentId = Guid.NewGuid(), Body = "Second comment" }\n    }\n};\n\n// Save the post\nrepo.Add(post);\n\n// When loading the Posting, Comments will be lazy-loaded\nPosting loadedPost = repo.Single<Posting>(p => p.InternalId == post.InternalId);\n\n// Access Comments to trigger lazy loading\nList<Comment> comments = loadedPost.Comments;