using System;\nusing System.ServiceProcess;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string[] computerList = { "Computer1", "Computer2", "Computer3" }; // Replace with your actual computer names\n\n        foreach (string computer in computerList)\n        {\n            CheckTerminalServices(computer);\n        }\n    }\n\n    static void CheckTerminalServices(string computerName)\n    {\n        try\n        {\n            ServiceController sc = new ServiceController("TermService", computerName);\n\n            if (sc.Status == ServiceControllerStatus.Running)\n            {\n                Console.WriteLine($"Terminal Services are running on {computerName}.");\n            }\n            else\n            {\n                Console.WriteLine($"Terminal Services are not running on {computerName}.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error checking Terminal Services on {computerName}: {ex.Message}");\n        }\n    }\n}