using System;\nusing System.Text;\nusing System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        string filename = "your_filename_here";\n        string password = "your_password_here";\n\n        using (var hmacsha256 = new HMACSHA256(Encoding.UTF8.GetBytes(password)))\n        {\n            byte[] hash = hmacsha256.ComputeHash(Encoding.UTF8.GetBytes(filename));\n            Console.WriteLine(BitConverter.ToString(hash).Replace("-", "").ToLower());\n        }\n    }\n}